import React, { useState, useContext, useEffect } from 'react';
import AuthContext from '../../contexts/AuthContext';
import axios from 'axios'; // For API calls in the future

const MemberProfilePage = () => {
  const { user, setUser: setAuthUser, token } = useContext(AuthContext); // Assuming setUser in AuthContext updates the global user state

  const [profileData, setProfileData] = useState({
    fullName: '',
    email: '',
    dateOfBirth: '',
    phoneNumber: '',
    address: '',
    occupation: '',
    // profilePictureUrl: '', // For file uploads, more complex handling needed
  });
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    if (user) {
      // In a real app, you would fetch this data from the backend
      // For now, we'll use the user data from AuthContext and add placeholders
      setProfileData({
        fullName: user.fullName || '',
        email: user.email || '',
        dateOfBirth: user.dateOfBirth || '', // Assuming these fields might exist or be fetched
        phoneNumber: user.phoneNumber || '',
        address: user.address || '',
        occupation: user.occupation || '',
      });
      setIsLoading(false);
    }
  }, [user]);

  const handleChange = (e) => {
    setProfileData({ ...profileData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    try {
      // Placeholder for API call
      console.log('Updating profile with:', profileData);
      // const response = await axios.put(`${process.env.REACT_APP_API_URL}/profile/me`, profileData, {
      //   headers: { Authorization: `Bearer ${token}` }
      // });
      // setAuthUser(prevUser => ({...prevUser, ...response.data.updatedProfile})); // Update context
      setSuccess('Profile updated successfully! (Simulated)');
      // Simulate updating AuthContext user (in real app, this comes from API response)
      setAuthUser(prevUser => ({...prevUser, fullName: profileData.fullName, email: profileData.email}));


    } catch (err) {
      setError(err.response?.data?.msg || 'Failed to update profile.');
      console.error('Profile update error:', err);
    }
  };

  if (isLoading) {
    return <p>Loading profile...</p>;
  }

  return (
    <div>
      <h2>My Profile</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="fullName">Full Name:</label>
          <input
            type="text"
            id="fullName"
            name="fullName"
            value={profileData.fullName}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={profileData.email}
            onChange={handleChange}
            required
          // readOnly // Email might not be editable or require special verification
          />
        </div>
        <div>
          <label htmlFor="dateOfBirth">Date of Birth:</label>
          <input
            type="date"
            id="dateOfBirth"
            name="dateOfBirth"
            value={profileData.dateOfBirth}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="phoneNumber">Phone Number:</label>
          <input
            type="tel"
            id="phoneNumber"
            name="phoneNumber"
            value={profileData.phoneNumber}
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="address">Address:</label>
          <textarea
            id="address"
            name="address"
            value={profileData.address}
            onChange={handleChange}
            rows="3"
          ></textarea>
        </div>
        <div>
          <label htmlFor="occupation">Occupation:</label>
          <input
            type="text"
            id="occupation"
            name="occupation"
            value={profileData.occupation}
            onChange={handleChange}
          />
        </div>
        {/* Add profile picture upload later if needed */}
        <button type="submit">Save Changes</button>
      </form>
    </div>
  );
};

export default MemberProfilePage;
