import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import AuthContext from '../../contexts/AuthContext';
import { Link } from 'react-router-dom';

// It's good practice to name the component file to match the component name.
// e.g., TrainerMemberManagementPage.jsx
const TrainerMemberManagementPage = () => {
    const [members, setMembers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');
    const auth = useContext(AuthContext);

    // --- FIX: Define fetchAssignedMembers in the main component scope ---
    // This function can now be called from anywhere inside the component.
    const fetchAssignedMembers = async () => {
      // We still need the check for the auth token.
      if (!auth || !auth.token) {
        setIsLoading(false);
        setError('Could not verify authentication. Please log in again.');
        return;
      }

      setIsLoading(true);
      setError('');

      try {
        const config = { headers: { Authorization: `Bearer ${auth.token}` } };
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/trainer/my-members`, config);
        setMembers(res.data);
      } catch (err) {
        setError('Không thể tải danh sách hội viên.');
        console.error("Error fetching assigned members:", err);
      } finally {
        setIsLoading(false);
      }
    };

    // The useEffect hook now calls the function from the outer scope.
    // It depends on `auth` so that if the user logs out and back in, the data is re-fetched.
    useEffect(() => {
        fetchAssignedMembers();
    }, [auth]);


    const handleDelete = async (memberId) => {
        // We can now call fetchAssignedMembers because it's in the same scope.
        if (window.confirm('Bạn có chắc chắn muốn xóa hội viên này? Hành động này không thể hoàn tác.')) {
            try {
                const config = { headers: { Authorization: `Bearer ${auth.token}` } };
                await axios.delete(`${process.env.REACT_APP_API_URL}/trainer/member/${memberId}`, config);
                alert('Xóa thành công!');
                fetchAssignedMembers(); // Re-fetch the list to show the change.
            } catch (err) {
                alert('Xóa thất bại.');
            }
        }
    };

    if (isLoading) return <p>Đang tải...</p>;
    if (error) return <p style={{color: 'red'}}>{error}</p>;

    return (
        <div>
            <Link to="/dashboard">← Quay lại Dashboard</Link>
            <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center'}}>
                <h2 style={{marginTop: '1rem'}}>Quản lý Danh sách Học viên</h2>
                {/* A trainer might not have permission to add members, this is more of a staff role.
                    But we can leave the link here if your business logic allows it. */}
                <Link to="/staff/register-member">
                    <button>Thêm Học viên mới</button>
                </Link>
            </div>

            <table className="feedback-table"> {/* Reusing styles from feedback page for consistency */}
                <thead>
                    <tr>
                        <th>Tên Học viên</th>
                        <th>Email</th>
                        <th>Gói tập hiện tại</th>
                        <th>Trạng thái</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    {members.length > 0 ? members.map(member => (
                        <tr key={member.user_id}>
                            {/* Using optional chaining (?.) to prevent errors if Profile is missing */}
                            <td>{member.Profile?.full_name || 'N/A'}</td>
                            <td>{member.email}</td>
                            {/* 
                                This logic finds the most recent subscription. 
                                We sort by end_date to be more accurate, assuming subscriptions can be out of order.
                            */}
                            <td>{member.MemberSubscriptions?.sort((a, b) => new Date(b.end_date) - new Date(a.end_date))[0]?.MembershipPackage?.package_name || 'N/A'}</td>
                            <td>{member.MemberSubscriptions?.sort((a, b) => new Date(b.end_date) - new Date(a.end_date))[0]?.status || 'N/A'}</td>
                            <td>
                                <button className="btn-view" style={{marginRight: '5px'}}>Xem</button>
                                <button className="btn-edit" style={{marginRight: '5px'}}>Sửa</button>
                                <button className="btn-delete" onClick={() => handleDelete(member.user_id)}>Xóa</button>
                            </td>
                        </tr>
                    )) : (
                        <tr><td colSpan="5" className="no-feedback-cell">Chưa có học viên nào được phân công.</td></tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default TrainerMemberManagementPage;
