import React, { useState, useEffect, useCallback, useContext } from 'react';
import axios from 'axios';
import AuthContext from '../../contexts/AuthContext';
import { Link } from 'react-router-dom';

const TrainerMemberManagementPage = () => {
    const [members, setMembers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');
    // --- NEW STATE ---
    const [view, setView] = useState('list'); // 'list', 'details', or 'edit'
    const [selectedMember, setSelectedMember] = useState(null);
    const [editFormData, setEditFormData] = useState({});

    const auth = useContext(AuthContext);

    // --- FIX: Define fetchAssignedMembers in the main component scope ---
    // This function can now be called from anywhere inside the component.
    const fetchAssignedMembers = async () => {
      // We still need the check for the auth token.
      if (!auth || !auth.token) {
        setIsLoading(false);
        setError('Could not verify authentication. Please log in again.');
        return;
      }

      setIsLoading(true);
      setError('');

      try {
        const config = { headers: { Authorization: `Bearer ${auth.token}` } };
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/trainer/my-members`, config);
        setMembers(res.data);
      } catch (err) {
        setError('Không thể tải danh sách hội viên.');
        console.error("Error fetching assigned members:", err);
      } finally {
        setIsLoading(false);
      }
    };

    // The useEffect hook now calls the function from the outer scope.
    // It depends on `auth` so that if the user logs out and back in, the data is re-fetched.
    useEffect(() => {
        fetchAssignedMembers();
    }, [auth]);

    const handleViewDetails = async (memberId) => {
        setIsLoading(true);
        try {
            const config = { headers: { Authorization: `Bearer ${auth.token}` } };
            const res = await axios.get(`${process.env.REACT_APP_API_URL}/trainer/member/${memberId}`, config);
            setSelectedMember(res.data);
            setView('details');
        } catch (err) {
            alert('Không thể tải chi tiết hội viên.');
        } finally {
            setIsLoading(false);
        }
    };
    
    const handleEdit = (member) => {
        setEditFormData({
            fullName: member.Profile.full_name,
            phoneNumber: member.Profile.phone_number,
            dateOfBirth: member.Profile.date_of_birth?.split('T')[0] || '' // Format for date input
        });
        setSelectedMember(member);
        setView('edit');
    };

    const handleUpdateSubmit = async (e) => {
        e.preventDefault();
        try {
            const config = { headers: { Authorization: `Bearer ${auth.token}` } };
            await axios.put(`${process.env.REACT_APP_API_URL}/trainer/member/${selectedMember.user_id}`, editFormData, config);
            alert('Cập nhật thành công!');
            setView('list');
            fetchAssignedMembers();
        } catch(err) {
            alert('Cập nhật thất bại.');
        }
    };

    const handleDelete = async (memberId) => {
        // ... (delete logic remains the same)
    };

    const renderListView = () => (
        <div>
            <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center'}}>
                <h2>Quản lý Danh sách Học viên</h2>
                <Link to="/staff/register-member"><button>Thêm Học viên mới</button></Link>
            </div>
            <table>
                {/* ... table headers ... */}
                <tbody>
                    {members.map(member => (
                        <tr key={member.user_id}>
                            <td>{member.Profile.full_name}</td>
                            <td>{member.email}</td>
                            <td>{member.MemberSubscriptions[0]?.MembershipPackage?.package_name || 'N/A'}</td>
                            <td>
                                <button onClick={() => handleViewDetails(member.user_id)}>Xem</button>
                                <button onClick={() => handleEdit(member)}>Sửa</button>
                                <button onClick={() => handleDelete(member.user_id)}>Xóa</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );

    const renderDetailView = () => (
        <div>
            <button onClick={() => setView('list')}>← Quay lại danh sách</button>
            <h2>Chi tiết Học viên: {selectedMember.Profile.full_name}</h2>
            <p><strong>Email:</strong> {selectedMember.email}</p>
            <p><strong>Ngày sinh:</strong> {new Date(selectedMember.Profile.date_of_birth).toLocaleDateString()}</p>
            <p><strong>Số điện thoại:</strong> {selectedMember.Profile.phone_number}</p>
            <h3>Lịch sử Gói tập</h3>
            <ul>
                {selectedMember.MemberSubscriptions.map(sub => (
                    <li key={sub.subscription_id}>
                        {sub.MembershipPackage.package_name} (Từ {new Date(sub.start_date).toLocaleDateString()} đến {new Date(sub.end_date).toLocaleDateString()})
                    </li>
                ))}
            </ul>
            {/* Add more details as needed */}
        </div>
    );

    const renderEditView = () => (
        <div>
            <button onClick={() => setView('list')}>← Hủy bỏ</button>
            <h2>Chỉnh sửa Học viên: {selectedMember.Profile.full_name}</h2>
            <form onSubmit={handleUpdateSubmit}>
                 <div>
                    <label>Họ tên:</label>
                    <input type="text" value={editFormData.fullName} onChange={e => setEditFormData({...editFormData, fullName: e.target.value})} />
                </div>
                <div>
                    <label>Số điện thoại:</label>
                    <input type="tel" value={editFormData.phoneNumber} onChange={e => setEditFormData({...editFormData, phoneNumber: e.target.value})} />
                </div>
                <div>
                    <label>Ngày sinh:</label>
                    <input type="date" value={editFormData.dateOfBirth} onChange={e => setEditFormData({...editFormData, dateOfBirth: e.target.value})} />
                </div>
                <button type="submit">Lưu thay đổi</button>
            </form>
        </div>
    );

    if (isLoading) return <p>Đang tải...</p>;
    if (error) return <p style={{color: 'red'}}>{error}</p>;

    return (
        <div>
            <Link to="/dashboard">← Quay lại Dashboard</Link>
            {view === 'list' && renderListView()}
            {view === 'details' && renderDetailView()}
            {view === 'edit' && renderEditView()}
        </div>
    );
};

export default TrainerMemberManagementPage;
