import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import AuthContext from '../../contexts/AuthContext'; // We still need this for the update action

const EquipmentStatusPage = () => {
  const [equipmentList, setEquipmentList] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  
  // We still get the auth object to use for the protected PUT request later
  const { auth } = useContext(AuthContext);

  // This useEffect is now much simpler. It runs once when the component mounts.
  useEffect(() => {
    const fetchEquipment = async () => {
      setIsLoading(true);
      try {
        // No authentication config is needed for this public GET request
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/equipment`);
        setEquipmentList(res.data);
      } catch (err) {
        setError('Failed to load equipment list.');
        console.error("Fetch equipment error:", err);
      } finally {
        setIsLoading(false);
      }
    };

    fetchEquipment();
  }, []); // The empty dependency array [] ensures this runs only ONCE.


  const handleUpdateStatus = async (equipmentId, newStatus) => {
    // Check for auth token BEFORE making the protected API call
    if (!auth || !auth.token) {
      return alert('You must be logged in to update equipment status.');
    }
    if (!newStatus) {
      return alert('Please select a valid status.');
    }

    try {
      // The PUT request still needs the authentication token
      const config = { headers: { Authorization: `Bearer ${auth.token}` } };
      const body = { status: newStatus };
      await axios.put(`${process.env.REACT_APP_API_URL}/equipment/${equipmentId}`, body, config);
      
      alert('Status updated successfully!');
      
      // Refresh the list after a successful update
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/equipment`);
      setEquipmentList(res.data);

    } catch (err) {
      alert('Failed to update status. You may not have permission.');
    }
  };

  // --- RENDER LOGIC ---
  if (isLoading) return <p>Loading equipment...</p>;
  if (error) return <p style={{color: 'red'}}>{error}</p>;

  return (
    <div>
      <h2>Update Equipment Status</h2>
      <table style={{width: '100%', borderCollapse: 'collapse'}}>
        <thead>
          <tr style={{backgroundColor: '#343a40', color: 'white'}}>
            <th style={{padding: '10px'}}>ID</th>
            <th style={{padding: '10px'}}>Name</th>
            <th style={{padding: '10px'}}>Current Status</th>
            <th style={{padding: '10px'}}>Update Status To:</th>
          </tr>
        </thead>
        <tbody>
          {equipmentList.length > 0 ? (
            equipmentList.map(item => (
              <tr key={item.equipment_id}>
                <td style={{padding: '10px', border: '1px solid #ddd'}}>{item.equipment_id}</td>
                <td style={{padding: '10px', border: '1px solid #ddd'}}>{item.equipment_name}</td>
                <td style={{padding: '10px', border: '1px solid #ddd'}}>
                  <span style={{fontWeight: 'bold', color: item.status === 'operational' ? 'green' : 'red'}}>
                    {item.status}
                  </span>
                </td>
                <td style={{padding: '10px', border: '1px solid #ddd'}}>
                  <select 
                    defaultValue={item.status} 
                    onChange={(e) => handleUpdateStatus(item.equipment_id, e.target.value)}
                  >
                    <option value={item.status} disabled>-- Select New Status --</option>
                    <option value="operational">Operational</option>
                    <option value="under_maintenance">Under Maintenance</option>
                    <option value="broken">Broken</option>
                  </select>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4" style={{textAlign: 'center', padding: '20px'}}>No equipment found.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default EquipmentStatusPage;
