import React from 'react';
import NavButtons from './NavButtons'; // đường dẫn tương ứng

const HomePage = () => {
  return (
    <div className="min-h-screen flex flex-col justify-center items-center text-gray-100 px-6 py-12" style={{ position: 'relative', zIndex: 1 }}>
      <div className="bg-gray-900 bg-opacity-80 p-8 rounded-lg max-w-xl text-center shadow-2xl
            transform transition-transform duration-700 ease-in-out
            hover:scale-105
            sm:p-12
            md:p-16">
        <h1 className="text-4xl sm:text-5xl md:text-6xl font-extrabold mb-6" style={{
          background: 'linear-gradient(90deg, #fffbe6 0%, #ffe066 40%, #ffd700 100%)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent',
          textShadow: '0 4px 24px rgba(255, 255, 200, 0.7), 0 1px 2px #fff',
          letterSpacing: '2px',
          fontWeight: 900,
        }}>
          Welcome to Gym Management System
        </h1>
        <p className="mb-10 text-base sm:text-lg md:text-xl text-gray-300">
          Your one-stop solution for managing your gym operations efficiently.
        </p>

        {/* Hiển thị 3 nút ở giữa trang */}
        <NavButtons />
      </div>
    </div>
  );
};

export default HomePage;
