import React, { useState, useEffect, useContext, useCallback } from 'react';
import axios from 'axios';
import AuthContext from '../../contexts/AuthContext'; // Ensure this path is correct
import { Link } from 'react-router-dom';

const EquipmentStatusPage = () => {
  const [equipmentList, setEquipmentList] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  
  // This now correctly gets the { auth, isLoading } object from our standardized context
  const { auth } = useContext(AuthContext);

  // The logic inside useCallback is refined for clarity and correctness
  const fetchEquipment = useCallback(async () => {
    // We only proceed if we have a token.
    if (!auth || !auth.token) {
      // If we are still in the initial loading phase, do nothing and wait.
      // If auth is loaded and there's no token, it means user is not logged in.
      setIsLoading(false); // Make sure we don't get stuck on "Loading..."
      setError('You must be logged in to view this page.'); // Inform the user
      return;
    }

    // If we have a token, proceed with fetching data.
    setIsLoading(true);
    setError('');

    try {
      const config = { headers: { Authorization: `Bearer ${auth.token}` } };
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/equipment`, config);
      setEquipmentList(res.data);
    } catch (err) {
      setError('Failed to load equipment list. You may not have the required permissions.');
      console.error("Fetch equipment error:", err);
    } finally {
      setIsLoading(false);
    }
  }, [auth]); // The only true dependency is the `auth` object itself.

  // This useEffect hook is now perfect. It calls fetchEquipment whenever it changes.
  useEffect(() => {
    fetchEquipment();
  }, [fetchEquipment]);

  const handleUpdateStatus = async (equipmentId, newStatus) => {
    // The check for auth token is still good practice here.
    if (!auth || !auth.token) {
      return alert('Authentication error. Please log in again.');
    }
    
    try {
      const config = { headers: { Authorization: `Bearer ${auth.token}` } };
      const body = { status: newStatus };
      await axios.put(`${process.env.REACT_APP_API_URL}/equipment/${equipmentId}`, body, config);
      
      alert('Cập nhật trạng thái thành công!');
      // After updating, we can simply re-run the same fetch function.
      fetchEquipment(); 
    } catch (err) {
      alert('Cập nhật thất bại.');
    }
  };

  // The render logic is updated to handle the `value` of the select correctly
  // to prevent React warnings and ensure the UI reflects the true state.
  if (isLoading) return <p>Đang tải danh sách thiết bị...</p>;

  return (
    <div>
      <Link to="/dashboard">← Quay lại Dashboard</Link>
      <h2 style={{marginTop: '1rem'}}>Cập nhật Tình trạng Thiết bị</h2>
      
      {error && <p style={{color: 'red'}}>{error}</p>}
      
      <table style={{width: '100%', borderCollapse: 'collapse'}}>
        <thead>
          <tr style={{backgroundColor: '#343a40', color: 'white'}}>
            <th style={{padding: '12px'}}>Tên Thiết bị</th>
            <th style={{padding: '12px'}}>Trạng thái Hiện tại</th>
            <th style={{padding: '12px'}}>Hành động (Chọn trạng thái mới)</th>
          </tr>
        </thead>
        <tbody>
          {equipmentList.length > 0 ? (
            equipmentList.map(item => (
              <tr key={item.equipment_id}>
                <td style={{padding: '12px', border: '1px solid #ddd'}}>{item.equipment_name}</td>
                <td style={{padding: '12px', border: '1px solid #ddd'}}>
                  <span style={{fontWeight: 'bold', color: item.status === 'operational' ? 'green' : (item.status === 'broken' ? 'red' : 'orange')}}>
                    {item.status}
                  </span>
                </td>
                <td style={{padding: '12px', border: '1px solid #ddd'}}>
                  <select 
                    value={item.status} // Using `value` is better for controlled components
                    onChange={(e) => handleUpdateStatus(item.equipment_id, e.target.value)}
                  >
                    {/* The current status is the selected value, no need for a disabled option */}
                    <option value="operational">Hoạt động (Operational)</option>
                    <option value="under_maintenance">Đang bảo trì (Under Maintenance)</option>
                    <option value="broken">Bị hỏng (Broken)</option>
                  </select>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="3" style={{textAlign: 'center', padding: '20px'}}>
                {/* Show a different message if there was an error vs. just no data */}
                {error ? 'Could not load data.' : 'Không có thiết bị nào.'}
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default EquipmentStatusPage;
