// File: src/pages/Trainer/TrainerMemberManagementPage.jsx
import React, { useState, useEffect, useCallback, useContext } from 'react';
import axios from 'axios';
import AuthContext from '../../contexts/AuthContext';
import { Link } from 'react-router-dom';

const TrainerMemberListPage = () => {
    const [members, setMembers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');
    const { auth } = useContext(AuthContext);

    useEffect(() => {
    // Define the async function inside the effect.
    const fetchAssignedMembers = async () => {
      // This is the crucial check. If the auth token isn't ready, we stop.
      if (!auth || !auth.token) {
        // Make sure we don't get stuck loading.
        setIsLoading(false);
        // We can set an error to be more user-friendly.
        setError('Could not verify authentication. Please try logging in again.');
        return;
      }

      // If we have a token, we can now safely proceed.
      setIsLoading(true);
      setError('');

      try {
        const config = { headers: { Authorization: `Bearer ${auth.token}` } };
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/trainer/my-members`, config);
        setMembers(res.data);
      } catch (err) {
        setError('Không thể tải danh sách hội viên.');
        console.error("Error fetching assigned members:", err);
      } finally {
        // This always runs, ensuring the "Loading..." message is removed.
        setIsLoading(false);
      }
    };

    fetchAssignedMembers();
  }, [auth]);

    const handleDelete = async (memberId) => {
        if (window.confirm('Bạn có chắc chắn muốn xóa hội viên này? Hành động này không thể hoàn tác.')) {
            try {
                const config = { headers: { Authorization: `Bearer ${auth.token}` } };
                await axios.delete(`${process.env.REACT_APP_API_URL}/trainer/member/${memberId}`, config);
                alert('Xóa thành công!');
                fetchAssignedMembers(); // Tải lại danh sách
            } catch (err) {
                alert('Xóa thất bại.');
            }
        }
    };

    if (isLoading) return <p>Đang tải...</p>;
    if (error) return <p style={{color: 'red'}}>{error}</p>;

    return (
        <div>
            <Link to="/dashboard">← Quay lại Dashboard</Link>
            <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center'}}>
                <h2 style={{marginTop: '1rem'}}>Quản lý Danh sách Học viên</h2>
                <Link to="/staff/register-member"> {/* Reuse the staff registration page */}
                    <button>Thêm Học viên mới</button>
                </Link>
            </div>

            <table>
                <thead>
                    <tr>
                        <th>Tên Học viên</th>
                        <th>Email</th>
                        <th>Gói tập hiện tại</th>
                        <th>Trạng thái</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    {members.length > 0 ? members.map(member => (
                        <tr key={member.user_id}>
                            <td>{member.Profile.full_name}</td>
                            <td>{member.email}</td>
                            {/* Logic to display the latest package - needs refinement */}
                            <td>{member.MemberSubscriptions[0]?.MembershipPackage?.package_name || 'N/A'}</td>
                            <td>{member.MemberSubscriptions[0]?.status || 'N/A'}</td>
                            <td>
                                <button>Xem</button>
                                <button>Sửa</button>
                                <button onClick={() => handleDelete(member.user_id)}>Xóa</button>
                            </td>
                        </tr>
                    )) : (
                        <tr><td colSpan="5">Chưa có học viên nào được phân công.</td></tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default TrainerMemberListPage;
