import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import AuthContext from '../../contexts/AuthContext'; // We still need this for the UPDATE action
import { Link } from 'react-router-dom';

const EquipmentStatusPage = () => {
  const [equipmentList, setEquipmentList] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState('');
  
  // We still get the context to use its values for the protected UPDATE action.
  const { user, token } = useContext(AuthContext); // Using the OLD context structure

  // This useEffect is now very simple. It runs once when the component mounts.
  useEffect(() => {
    const fetchEquipment = async () => {
      setIsLoading(true);
      try {
        // No authentication config is needed for this public GET request.
        const res = await axios.get(`${process.env.REACT_APP_API_URL}/equipment`);
        setEquipmentList(res.data);
      } catch (err) {
        setError('Không thể tải danh sách thiết bị.');
        console.error("Fetch equipment error:", err);
      } finally {
        setIsLoading(false);
      }
    };

    fetchEquipment();
  }, []); // The empty dependency array [] ensures this runs only ONCE.


  const handleUpdateStatus = async (equipmentId, newStatus) => {
    // Check for auth token BEFORE making the protected API call
    if (!token) {
      return alert('Bạn phải đăng nhập để cập nhật trạng thái.');
    }
    if (!newStatus) {
      return alert('Vui lòng chọn trạng thái hợp lệ.');
    }

    try {
      // The PUT request still needs the authentication token
      const config = { headers: { Authorization: `Bearer ${token}` } };
      const body = { status: newStatus };
      await axios.put(`${process.env.REACT_APP_API_URL}/equipment/${equipmentId}`, body, config);
      
      alert('Cập nhật trạng thái thành công!');
      
      // Refresh the list after a successful update by re-fetching
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/equipment`);
      setEquipmentList(res.data);

    } catch (err) {
      alert('Cập nhật thất bại. Bạn có thể không có quyền.');
    }
  };

  // --- RENDER LOGIC ---
  if (isLoading) return <p>Đang tải danh sách thiết bị...</p>;
  if (error) return <p style={{color: 'red'}}>{error}</p>;

  return (
    <div>
      <Link to="/dashboard">← Quay lại Dashboard</Link>
      <h2 style={{marginTop: '1rem'}}>Cập nhật Tình trạng Thiết bị</h2>
      <table style={{width: '100%', borderCollapse: 'collapse'}}>
        <thead>
          <tr style={{backgroundColor: '#343a40', color: 'white'}}>
            <th style={{padding: '12px'}}>Tên Thiết bị</th>
            <th style={{padding: '12px'}}>Trạng thái Hiện tại</th>
            <th style={{padding: '12px'}}>Hành động (Chọn trạng thái mới)</th>
          </tr>
        </thead>
        <tbody>
          {equipmentList.length > 0 ? (
            equipmentList.map(item => (
              <tr key={item.equipment_id}>
                <td style={{padding: '12px', border: '1px solid #ddd'}}>{item.equipment_name}</td>
                <td style={{padding: '12px', border: '1px solid #ddd'}}>
                  <span style={{fontWeight: 'bold', color: item.status === 'operational' ? 'green' : (item.status === 'broken' ? 'red' : 'orange')}}>
                    {item.status}
                  </span>
                </td>
                <td style={{padding: '12px', border: '1px solid #ddd'}}>
                  <select 
                    value={item.status} 
                    onChange={(e) => handleUpdateStatus(item.equipment_id, e.target.value)}
                  >
                    <option value="operational">Hoạt động (Operational)</option>
                    <option value="under_maintenance">Đang bảo trì (Under Maintenance)</option>
                    <option value="broken">Bị hỏng (Broken)</option>
                  </select>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="3" style={{textAlign: 'center', padding: '20px'}}>Không có thiết bị nào.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default EquipmentStatusPage;
