import React from 'react';
import gymBackground from '../assets/images/gym-background.jpg';

const HomePage = () => {
  return (
    <>
      {/* Fullscreen background image with overlay */}
      <div
        style={{
          position: 'fixed',
          inset: 0,
          width: '100vw',
          height: '100vh',
          backgroundImage: `url(${gymBackground})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          zIndex: -2,
        }}
      />
      {/* Dark overlay for readability */}
      <div
        style={{
          position: 'fixed',
          inset: 0,
          width: '100vw',
          height: '100vh',
          background: 'rgba(20, 20, 30, 0.7)',
          zIndex: -1,
        }}
      />

      {/* Content overlay */}
      <div
        className="min-h-screen flex flex-col justify-center items-center text-gray-100 px-6 py-12"
        style={{ position: 'relative', zIndex: 1 }}
      >
        <div className="bg-gray-900 bg-opacity-80 p-8 rounded-lg max-w-xl text-center shadow-2xl
          transform transition-transform duration-700 ease-in-out
          hover:scale-105
          sm:p-12
          md:p-16
          "
        >
          <h1 className="text-3xl sm:text-4xl md:text-5xl font-extrabold mb-6 text-yellow-300 animate-fadeInDown">
            Welcome to Gym Management System
          </h1>
          <p className="mb-10 text-base sm:text-lg md:text-xl text-gray-300 animate-fadeInUp">
            Your one-stop solution for managing your gym operations efficiently.
          </p>
          <div className="flex flex-col sm:flex-row justify-center space-y-4 sm:space-y-0 sm:space-x-8 animate-fadeInUp delay-200">
          
          </div>
        </div>
      </div>

      {/* Custom animation styles */}
      <style>{`
        @keyframes fadeInDown {
          0% {
            opacity: 0;
            transform: translateY(-20px);
          }
          100% {
            opacity: 1;
            transform: translateY(0);
          }
        }
        @keyframes fadeInUp {
          0% {
            opacity: 0;
            transform: translateY(20px);
          }
          100% {
            opacity: 1;
            transform: translateY(0);
          }
        }
        .animate-fadeInDown {
          animation: fadeInDown 0.8s ease forwards;
        }
        .animate-fadeInUp {
          animation: fadeInUp 0.8s ease forwards;
        }
        .delay-200 {
          animation-delay: 0.2s;
        }
      `}</style>
    </>
  );
};

export default HomePage;
