import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const DashboardPage = () => {
    const [user, setUser] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const storedUser = localStorage.getItem('user');
        const token = localStorage.getItem('token');

        if (!token || !storedUser) {
            navigate('/login');
            return;
        }

        try {
            setUser(JSON.parse(storedUser));
        } catch (error) {
            console.error("Failed to parse user from localStorage", error);
            localStorage.removeItem('user');
            localStorage.removeItem('token');
            navigate('/login');
        }
    }, [navigate]);

    if (!user) {
        return <p>Loading dashboard...</p>; // Or a loading spinner
    }

    // Render different content based on user role
    const renderDashboardContent = () => {
        switch (user.role) {
            case 'member':
                return <h3>Welcome Member, {user.fullName}! This is your dashboard.</h3>;
            case 'staff':
                return <h3>Welcome Staff, {user.fullName}! This is your dashboard.</h3>;
            case 'trainer':
                return <h3>Welcome Trainer, {user.fullName}! This is your dashboard.</h3>;
            case 'owner':
                return <h3>Welcome Owner, {user.fullName}! This is your admin dashboard.</h3>;
            default:
                return <h3>Welcome, {user.fullName}! Your role is undefined.</h3>;
        }
    };

    return (
        <div>
            <h2>Dashboard</h2>
            {renderDashboardContent()}
            {/* Add role-specific components/links here */}
        </div>
    );
};

export default DashboardPage;
