// In src/utils/PrivateRoute.jsx
import React, { useContext } from 'react';
import { Navigate, useLocation } from 'react-router-dom';
import AuthContext from '../contexts/AuthContext';

const PrivateRoute = ({ children, allowedRoles }) => {
  // Now we consume `auth` and `isLoading`
  const { auth, isLoading } = useContext(AuthContext);
  const location = useLocation();

  if (isLoading) {
    return <p>Loading Authentication...</p>; // Or a spinner
  }

  // If loading is done and there's no auth object (or no token), redirect
  if (!auth || !auth.token) {
    return <Navigate to="/login" state={{ from: location }} replace />;
  }

  // If roles are specified, check the user's role
  // Note: we access the user object inside the auth object: auth.user.role
  if (allowedRoles && !allowedRoles.includes(auth.user.role)) {
    return <Navigate to="/dashboard" replace />;
  }

  // If all checks pass, render the protected component
  return children;
};

export default PrivateRoute;
