import React, { useState, useEffect, useContext, useCallback } from 'react';
import axios from 'axios';
import AuthContext from '../../contexts/AuthContext'; // Make sure this path is correct

const EquipmentStatusPage = () => {
  const [equipmentList, setEquipmentList] = useState([]);
  const [isLoading, setIsLoading] = useState(true); // Always start in a loading state
  const [error, setError] = useState('');
  
  const { auth } = useContext(AuthContext);

  // Define the data fetching function using useCallback for optimization
  const fetchEquipment = useCallback(async () => {
    // This is the crucial check. If there's no token yet, we stop and wait.
    // The useEffect hook will re-run this function once `auth` is populated.
    if (!auth || !auth.token) {
      // If the component is still waiting for auth, we shouldn't show an error.
      // We also ensure it doesn't get stuck loading.
      if (isLoading) setIsLoading(false);
      return;
    }

    // If we have a token, THEN we set loading to true and make the API call.
    setIsLoading(true);
    setError('');

    try {
      // The API call MUST include the authentication token in the headers.
      const config = { headers: { Authorization: `Bearer ${auth.token}` } };
      const res = await axios.get(`${process.env.REACT_APP_API_URL}/equipment`, config);
      setEquipmentList(res.data);
    } catch (err) {
      setError('Failed to load equipment list. You may not have the required permissions.');
      console.error("Fetch equipment error:", err);
    } finally {
      // This will always run, ensuring the "Loading..." message goes away.
      setIsLoading(false);
    }
  }, [auth, isLoading]); // Dependency on auth is correct. isLoading is added for robustness.

  // The useEffect hook now simply calls our memoized function.
  useEffect(() => {
    fetchEquipment();
  }, [fetchEquipment]);

  const handleUpdateStatus = async (equipmentId, newStatus) => {
    if (!newStatus) {
        return alert('Please select a valid status.');
    }
    try {
      const config = { headers: { Authorization: `Bearer ${auth.token}` } };
      const body = { status: newStatus };
      await axios.put(`${process.env.REACT_APP_API_URL}/equipment/${equipmentId}`, body, config);
      
      alert('Status updated successfully!');
      fetchEquipment(); // Re-fetch the list to show the new status.
    } catch (err) {
      alert('Failed to update status.');
    }
  };

  // --- RENDER LOGIC ---
  if (isLoading) return <p>Loading equipment...</p>;
  if (error) return <p style={{color: 'red'}}>{error}</p>;

  return (
    <div>
      <h2>Update Equipment Status</h2>
      <table style={{width: '100%', borderCollapse: 'collapse'}}>
        <thead>
          <tr style={{backgroundColor: '#343a40', color: 'white'}}>
            <th style={{padding: '10px'}}>ID</th>
            <th style={{padding: '10px'}}>Name</th>
            <th style={{padding: '10px'}}>Current Status</th>
            <th style={{padding: '10px'}}>Update Status To:</th>
          </tr>
        </thead>
        <tbody>
          {equipmentList.length > 0 ? (
            equipmentList.map(item => (
              <tr key={item.equipment_id}>
                <td style={{padding: '10px', border: '1px solid #ddd'}}>{item.equipment_id}</td>
                <td style={{padding: '10px', border: '1px solid #ddd'}}>{item.equipment_name}</td>
                <td style={{padding: '10px', border: '1px solid #ddd'}}>
                  <span style={{fontWeight: 'bold', color: item.status === 'operational' ? 'green' : 'red'}}>
                    {item.status}
                  </span>
                </td>
                <td style={{padding: '10px', border: '1px solid #ddd'}}>
                  <select 
                    defaultValue={item.status} 
                    onChange={(e) => handleUpdateStatus(item.equipment_id, e.target.value)}
                  >
                    <option value={item.status} disabled>-- Select New Status --</option>
                    <option value="operational">Operational</option>
                    <option value="under_maintenance">Under Maintenance</option>
                    <option value="broken">Broken</option>
                  </select>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4" style={{textAlign: 'center', padding: '20px'}}>No equipment found or you do not have permission to view it.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default EquipmentStatusPage;
