import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import AuthContext from '../../contexts/AuthContext';

const SystemSettingsPage = () => {
    const [settings, setSettings] = useState({
        gym_name: '',
        address: '',
        contact_phone: '',
        contact_email: '',
    });
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const  auth  = useContext(AuthContext);

    useEffect(() => {
        const fetchSettings = async () => {
            if (!auth || !auth.token) return;
            setIsLoading(true);
            try {
                const config = { headers: { Authorization: `Bearer ${auth.token}` } };
                const res = await axios.get(`${process.env.REACT_APP_API_URL}/settings`, config);
                setSettings(res.data);
            } catch (err) {
                setError('Không thể tải thông tin cài đặt.');
            } finally {
                setIsLoading(false);
            }
        };
        fetchSettings();
    }, [auth]);

    const handleChange = (e) => {
        setSettings({
            ...settings,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setSuccess('');
        setError('');
        try {
            const config = { headers: { Authorization: `Bearer ${auth.token}` } };
            const res = await axios.put(`${process.env.REACT_APP_API_URL}/settings`, settings, config);
            setSuccess(res.data.msg);
        } catch (err) {
            setError(err.response?.data?.msg || 'Cập nhật thất bại.');
        }
    };

    if (isLoading) return <p>Đang tải...</p>;

    return (
        <div className="management-page-container">
            <Link to="/dashboard">← Quay lại Dashboard</Link>
            <h2 style={{marginTop: '1rem'}}>Quản lý Thông tin Hệ thống</h2>

            {error && <p style={{color: 'red'}}>{error}</p>}
            {success && <p style={{color: 'green'}}>{success}</p>}

            <form onSubmit={handleSubmit} className="edit-form">
                <div className="form-group">
                    <label>Tên phòng tập:</label>
                    <input type="text" name="gym_name" value={settings.gym_name} onChange={handleChange} required />
                </div>
                <div className="form-group">
                    <label>Địa chỉ:</label>
                    <input type="text" name="address" value={settings.address} onChange={handleChange} required />
                </div>
                <div className="form-group">
                    <label>Số điện thoại liên hệ:</label>
                    <input type="tel" name="contact_phone" value={settings.contact_phone} onChange={handleChange} required />
                </div>
                <div className="form-group">
                    <label>Email liên hệ:</label>
                    <input type="email" name="contact_email" value={settings.contact_email} onChange={handleChange} required />
                </div>
                <button type="submit">Lưu thay đổi</button>
            </form>
        </div>
    );
};

export default SystemSettingsPage;
