// File: src/pages/Staff/RecordServiceUsagePage.jsx
import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import AuthContext from '../../contexts/AuthContext';

const RecordServiceUsagePage = () => {
  const [members, setMembers] = useState([]);
  const [selectedMemberId, setSelectedMemberId] = useState('');
  const [serviceType, setServiceType] = useState('Gym entry');
  const [duration, setDuration] = useState(60);
  const [notes, setNotes] = useState('');

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const { auth } = useContext(AuthContext);

  // Tải danh sách hội viên khi trang được mở
  useEffect(() => {
    const fetchMembers = async () => {
      // Logic để lấy danh sách hội viên (có thể cần một API mới)
      // Tạm thời, chúng ta sẽ để trống và yêu cầu nhập ID thủ công
    };
    fetchMembers();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setError('');
    setSuccess('');

    if (!selectedMemberId || !serviceType) {
      setError('Vui lòng chọn hội viên và loại dịch vụ.');
      setIsSubmitting(false);
      return;
    }

    try {
      const config = { headers: { Authorization: `Bearer ${auth.token}` } };
      const body = {
        memberId: Number(selectedMemberId),
        serviceDate: new Date().toISOString(), // Ghi nhận thời gian hiện tại
        serviceType,
        durationMinutes: Number(duration),
        notes,
      };

      const res = await axios.post(`${process.env.REACT_APP_API_URL}/service-history`, body, config);
      setSuccess(res.data.msg);
      // Xóa form để chuẩn bị cho lần nhập tiếp theo
      setSelectedMemberId('');
      setNotes('');
    } catch (err) {
      setError(err.response?.data?.msg || 'Ghi nhận thất bại.');
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div>
      <Link to="/dashboard">← Quay lại Dashboard</Link>
      <h2 style={{ marginTop: '1rem' }}>Ghi nhận Lịch sử Dịch vụ</h2>
      
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}

      <form onSubmit={handleSubmit}>
        <div>
          <label>ID Hội viên:</label>
          <input
            type="number"
            placeholder="Nhập ID của hội viên..."
            value={selectedMemberId}
            onChange={(e) => setSelectedMemberId(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Loại Dịch vụ:</label>
          <select value={serviceType} onChange={(e) => setServiceType(e.target.value)}>
            <option value="Gym entry">Vào phòng gym</option>
            <option value="Yoga class">Tham gia lớp Yoga</option>
            <option value="Sauna">Sử dụng Sauna</option>
            <option value="Personal Training">Tập với PT</option>
          </select>
        </div>
        <div>
          <label>Thời lượng (phút):</label>
          <input
            type="number"
            value={duration}
            onChange={(e) => setDuration(e.target.value)}
          />
        </div>
        <div>
          <label>Ghi chú (tùy chọn):</label>
          <textarea
            value={notes}
            onChange={(e) => setNotes(e.target.value)}
            rows="3"
          />
        </div>
        <button type="submit" disabled={isSubmitting}>
          {isSubmitting ? 'Đang ghi nhận...' : 'Ghi nhận'}
        </button>
      </form>
    </div>
  );
};

export default RecordServiceUsagePage;
