import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import AuthContext from '../../contexts/AuthContext';
import axios from 'axios';

const ServiceUserRatingPage = () => {
  const { user, token } = useContext(AuthContext);
  const [ratings, setRatings] = useState({
    equipment: 5,
    cleanliness: 5,
    staff: 5,
    trainer: 5,
    overall: 5
  });
  const [feedback, setFeedback] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleRatingChange = (category, value) => {
    setRatings(prev => ({
      ...prev,
      [category]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');

    try {
      // In a real app, you would send this to your backend
      // const response = await axios.post(
      //   `${process.env.REACT_APP_API_URL}/feedback/submit`,
      //   {
      //     userId: user.id,
      //     ratings,
      //     feedback
      //   },
      //   {
      //     headers: { Authorization: `Bearer ${token}` }
      //   }
      // );

      // For now, we'll simulate a successful submission
      console.log('Submitting feedback:', { ratings, feedback });
      setSuccess('Cảm ơn bạn đã gửi đánh giá!');
      
      // Reset form after successful submission
      setRatings({
        equipment: 5,
        cleanliness: 5,
        staff: 5,
        trainer: 5,
        overall: 5
      });
      setFeedback('');
    } catch (err) {
      setError(err.response?.data?.msg || 'Không thể gửi đánh giá. Vui lòng thử lại sau.');
    }
  };

  const renderStarRating = (category, value) => {
    return (
      <div className="star-rating">
        {[1, 2, 3, 4, 5].map((star) => (
          <span
            key={star}
            onClick={() => handleRatingChange(category, star)}
            style={{
              cursor: 'pointer',
              fontSize: '24px',
              color: star <= value ? '#ffd700' : '#ccc',
              marginRight: '5px'
            }}
          >
            ★
          </span>
        ))}
      </div>
    );
  };

  return (
    <div>
      <Link to="/dashboard" style={{ display: 'inline-block', marginBottom: '20px', padding: '10px 15px', backgroundColor: '#6c757d', color: 'white', textDecoration: 'none', borderRadius: '4px' }}>
        &larr; Back to Dashboard
      </Link>

      <h2>Đánh giá chất lượng dịch vụ</h2>
      
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}

      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '20px' }}>
          <label>Chất lượng thiết bị:</label>
          {renderStarRating('equipment', ratings.equipment)}
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label>Vệ sinh phòng tập:</label>
          {renderStarRating('cleanliness', ratings.cleanliness)}
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label>Thái độ nhân viên:</label>
          {renderStarRating('staff', ratings.staff)}
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label>Chất lượng huấn luyện viên:</label>
          {renderStarRating('trainer', ratings.trainer)}
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label>Đánh giá tổng thể:</label>
          {renderStarRating('overall', ratings.overall)}
        </div>

        <div style={{ marginBottom: '20px' }}>
          <label htmlFor="feedback">Nhận xét chi tiết:</label>
          <textarea
            id="feedback"
            value={feedback}
            onChange={(e) => setFeedback(e.target.value)}
            rows="4"
            style={{ width: '100%', padding: '8px', marginTop: '5px' }}
            placeholder="Nhập nhận xét của bạn về dịch vụ..."
          />
        </div>

        <button 
          type="submit"
          style={{
            padding: '10px 20px',
            backgroundColor: '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Gửi đánh giá
        </button>
      </form>
    </div>
  );
};

export default ServiceUserRatingPage;
