import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import AuthContext from '../../contexts/AuthContext';
import { Link } from 'react-router-dom';

const TrackWorkoutPage = () => {
    const [members, setMembers] = useState([]);
    const [selectedMember, setSelectedMember] = useState(null);
    const [sessions, setSessions] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');
    const  auth  = useContext(AuthContext);

    const [newSessionData, setNewSessionData] = useState({
        sessionDatetime: '',
        exercisePlan: '',
        durationMinutes: 60,
        notes: ''
    });

    // This function now lives in the main scope.
    const fetchAssignedMembers = async () => {
        if (!auth || !auth.token) {
            setIsLoading(false);
            setError('Could not verify authentication.');
            return;
        }
        setIsLoading(true);
        try {
            const config = { headers: { Authorization: `Bearer ${auth.token}` } };
            const res = await axios.get(`${process.env.REACT_APP_API_URL}/trainer/members`, config);
            setMembers(res.data);
        } catch (err) {
            setError('Không thể tải danh sách hội viên.');
        } finally {
            setIsLoading(false);
        }
    };

    // This useEffect hook calls the function above.
    // It depends on `auth` to re-fetch if the user logs in/out.
    useEffect(() => {
        fetchAssignedMembers();
    }, [auth]);

    const handleMemberSelect = async (member) => {
        setSelectedMember(member);
        setIsLoading(true);
        try {
            const config = { headers: { Authorization: `Bearer ${auth.token}` } };
            const res = await axios.get(`${process.env.REACT_APP_API_URL}/trainer/member/${member.user_id}/sessions`, config);
            setSessions(res.data);
        } catch(err) {
            setError('Không thể tải lịch tập của hội viên này.');
        } finally {
            setIsLoading(false);
        }
    };

    const handleAddSessionSubmit = async (e) => {
        e.preventDefault();
        try {
            const config = { headers: { Authorization: `Bearer ${auth.token}` } };
            const body = { ...newSessionData, memberId: selectedMember.user_id };
            await axios.post(`${process.env.REACT_APP_API_URL}/trainer/sessions`, body, config);
            alert('Thêm buổi tập thành công!');
            setNewSessionData({ sessionDatetime: '', exercisePlan: '', durationMinutes: 60, notes: '' });
            handleMemberSelect(selectedMember); // Refresh session list
        } catch (err) {
            alert('Thêm buổi tập thất bại.');
        }
    };

    const handleStatusUpdate = async (sessionId, newStatus) => {
        try {
            const config = { headers: { Authorization: `Bearer ${auth.token}` } };
            const body = { status: newStatus };
            await axios.put(`${process.env.REACT_APP_API_URL}/trainer/sessions/${sessionId}`, body, config);
            alert('Cập nhật trạng thái thành công!');
            handleMemberSelect(selectedMember); // Refresh session list
        } catch (err) {
            alert('Cập nhật thất bại.');
        }
    };

    if (isLoading) return <p>Đang tải...</p>;
    if (error) return <p style={{color: 'red'}}>{error}</p>;

    return (
        <div>
            <Link to="/dashboard">← Quay lại Dashboard</Link>
            <h2>Theo dõi và Cập nhật Lịch tập</h2>

            {/* View to select a member */}
            {!selectedMember && (
                <div>
                    <h4>Vui lòng chọn một học viên để quản lý lịch tập:</h4>
                    <div className="member-selection-list">
                        {members.length > 0 ? members.map(member => (
                            <button key={member.user_id} onClick={() => handleMemberSelect(member)} className="member-select-button">
                                {member.Profile.full_name}
                            </button>
                        )) : <p>Không có học viên nào được phân công.</p>}
                    </div>
                </div>
            )}
            
            {/* View to manage a selected member's schedule */}
            {selectedMember && (
                <div>
                    <button onClick={() => setSelectedMember(null)} className="btn-back-dashboard">← Chọn học viên khác</button>
                    <h3>Lịch tập của: {selectedMember.Profile.full_name}</h3>
                    
                    <div className="add-session-form" style={{border: '1px solid #ccc', padding: '1rem', margin: '1rem 0'}}>
                        <h4>Thêm buổi tập mới</h4>
                        <form onSubmit={handleAddSessionSubmit}>
                            <input type="datetime-local" value={newSessionData.sessionDatetime} onChange={e => setNewSessionData({...newSessionData, sessionDatetime: e.target.value})} required />
                            <textarea placeholder="Nội dung bài tập (VD: Ngực, Vai, Tay sau)" value={newSessionData.exercisePlan} onChange={e => setNewSessionData({...newSessionData, exercisePlan: e.target.value})} required></textarea>
                            <input type="number" placeholder="Thời lượng (phút)" value={newSessionData.durationMinutes} onChange={e => setNewSessionData({...newSessionData, durationMinutes: e.target.value})} required />
                            <input type="text" placeholder="Ghi chú" value={newSessionData.notes} onChange={e => setNewSessionData({...newSessionData, notes: e.target.value})} />
                            <button type="submit">Thêm Buổi Tập</button>
                        </form>
                    </div>

                    <div className="session-list">
                        <h4>Các buổi tập đã lên lịch</h4>
                        {sessions.length > 0 ? sessions.map(session => (
                            <div key={session.session_id} style={{border: '1px solid #eee', padding: '1rem', marginBottom: '1rem'}}>
                                <p><strong>Ngày:</strong> {new Date(session.sessionDatetime).toLocaleString('vi-VN')}</p>
                                <p><strong>Nội dung:</strong> {session.exercisePlan}</p>
                                <p><strong>Trạng thái:</strong> {session.status}</p>
                                {session.trainerNotes && <p><strong>Ghi chú:</strong> {session.trainerNotes}</p>}
                                {session.status === 'Planned' && (
                                    <div>
                                        <button onClick={() => handleStatusUpdate(session.session_id, 'Completed')} style={{marginRight: '10px'}}>Đánh dấu Hoàn thành</button>
                                        <button onClick={() => handleStatusUpdate(session.session_id, 'Missed')} style={{backgroundColor: '#ffc107'}}>Đánh dấu Vắng</button>
                                    </div>
                                )}
                            </div>
                        )) : <p>Chưa có buổi tập nào được lên lịch cho học viên này.</p>}
                    </div>
                </div>
            )}
        </div>
    );
};

export default TrackWorkoutPage;
