import React, { useState, useEffect, useContext } from 'react';
import axios from 'axios';
import AuthContext from '../../contexts/AuthContext';
import { Link } from 'react-router-dom';

const TrainerMemberManagementPage = () => {
    const [members, setMembers] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState('');
    const  auth  = useContext(AuthContext);

    // State to manage which view is active: 'list', 'details', or 'edit'
    const [view, setView] = useState('list');
    const [selectedMember, setSelectedMember] = useState(null); // Holds data for the member being viewed/edited
    const [editFormData, setEditFormData] = useState({}); // Holds form data for editing


    // --- NEW STATE for the "Assign Member" modal ---
    const [isAssignModalVisible, setIsAssignModalVisible] = useState(false);
    const [searchQuery, setSearchQuery] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const [isSearching, setIsSearching] = useState(false);

    // Fetches the list of members assigned to the trainer
    const fetchAssignedMembers = async () => {
        if (!auth || !auth.token) {
            setIsLoading(false);
            setError('Could not verify authentication. Please log in again.');
            return;
        }
        setIsLoading(true);
        try {
            const config = { headers: { Authorization: `Bearer ${auth.token}` } };
            const res = await axios.get(`${process.env.REACT_APP_API_URL}/trainer/members`, config);
            setMembers(res.data);
        } catch (err) {
            setError('Không thể tải danh sách hội viên.');
        } finally {
            setIsLoading(false);
        }
    };

    useEffect(() => {
        fetchAssignedMembers();
    }, [auth]);

    // Fetches full details for one member and switches to the 'details' view
    const handleViewDetails = async (memberId) => {
        setIsLoading(true);
        try {
            const config = { headers: { Authorization: `Bearer ${auth.token}` } };
            const res = await axios.get(`${process.env.REACT_APP_API_URL}/trainer/member/${memberId}`, config);
            setSelectedMember(res.data);
            setView('details');
        } catch (err) {
            alert('Không thể tải chi tiết hội viên.');
        } finally {
            setIsLoading(false);
        }
    };

    // Pre-populates the edit form and switches to the 'edit' view
    const handleEditClick = (member) => {
        setSelectedMember(member);
        setEditFormData({
            fullName: member.Profile.full_name,
            phoneNumber: member.Profile.phone_number,
            dateOfBirth: member.Profile.date_of_birth ? new Date(member.Profile.date_of_birth).toISOString().split('T')[0] : ''
        });
        setView('edit');
    };

    // Handles changes in the edit form inputs
    const handleEditFormChange = (e) => {
        setEditFormData({ ...editFormData, [e.target.name]: e.target.value });
    };

    // Submits the updated member data to the backend
    const handleUpdateSubmit = async (e) => {
        e.preventDefault();
        try {
            const config = { headers: { Authorization: `Bearer ${auth.token}` } };
            await axios.put(`${process.env.REACT_APP_API_URL}/trainer/member/${selectedMember.user_id}`, editFormData, config);
            alert('Cập nhật thành công!');
            setView('list');
            fetchAssignedMembers(); // Refresh the list
        } catch (err) {
            alert('Cập nhật thất bại.');
        }
    };

    // Deletes a member after confirmation
    const handleDelete = async (memberId) => {
        if (window.confirm('Bạn có chắc chắn muốn xóa hội viên này? Hành động này không thể hoàn tác.')) {
            try {
                const config = { headers: { Authorization: `Bearer ${auth.token}` } };
                await axios.delete(`${process.env.REACT_APP_API_URL}/trainer/member/${memberId}`, config);
                alert('Xóa thành công!');
                fetchAssignedMembers(); // Refresh the list
            } catch (err) {
                alert('Xóa thất bại.');
            }
        }
    };

    const handleSearchChange = async (query) => {
        setSearchQuery(query);
        if (query.length < 2) {
            setSearchResults([]);
            return;
        }
        setIsSearching(true);
        try {
            const config = { headers: { Authorization: `Bearer ${auth.token}` } };
            const res = await axios.get(`${process.env.REACT_APP_API_URL}/users/search?q=${query}`, config);
            setSearchResults(res.data);
        } catch (err) {
            console.error("Search failed:", err);
        } finally {
            setIsSearching(false);
        }
    };
    
    const handleAssignMember = async (memberToAssign) => {
        if (window.confirm(`Bạn có chắc muốn phân công học viên "${memberToAssign.Profile.full_name}" cho mình?`)) {
            try {
                const config = { headers: { Authorization: `Bearer ${auth.token}` } };
                const bookingData = {
                    trainer_user_id: auth.user.user_id,
                    member_user_id: memberToAssign.user_id,
                    session_datetime: new Date().toISOString(),
                    notes_member: 'Initial assignment by trainer.'
                };
                await axios.post(`${process.env.REACT_APP_API_URL}/bookings`, bookingData, config);
                
                alert('Phân công thành công!');
                setIsAssignModalVisible(false);
                setSearchQuery('');
                setSearchResults([]);
                fetchAssignedMembers();
            } catch (err) {
                alert('Phân công thất bại.');
            }
        }
    };
    // Main render function
    if (isLoading) return <p>Đang tải...</p>;
    if (error) return <p style={{ color: 'red' }}>{error}</p>;

    // --- Conditional Rendering Logic ---

    // RENDER THE EDIT FORM
    if (view === 'edit') {
        return (
            <div className="management-page-container">
                <button className="btn-back-dashboard" onClick={() => setView('list')}>← Hủy bỏ và Quay lại</button>
                <h2>Chỉnh sửa Học viên: {selectedMember.Profile.full_name}</h2>
                <form onSubmit={handleUpdateSubmit} className="edit-form">
                    <div>
                        <label>Họ tên:</label>
                        <input name="fullName" type="text" value={editFormData.fullName} onChange={handleEditFormChange} />
                    </div>
                    <div>
                        <label>Số điện thoại:</label>
                        <input name="phoneNumber" type="tel" value={editFormData.phoneNumber} onChange={handleEditFormChange} />
                    </div>
                    <div>
                        <label>Ngày sinh:</label>
                        <input name="dateOfBirth" type="date" value={editFormData.dateOfBirth} onChange={handleEditFormChange} />
                    </div>
                    <button type="submit">Lưu thay đổi</button>
                </form>
            </div>
        );
    }

    // RENDER THE DETAIL VIEW
    if (view === 'details') {
        return (
            <div className="management-page-container">
                <button className="btn-back-dashboard" onClick={() => setView('list')}>← Quay lại danh sách</button>
                <h2>Chi tiết Học viên: {selectedMember.Profile.full_name}</h2>
                <p><strong>Email:</strong> {selectedMember.email}</p>
                <p><strong>Ngày sinh:</strong> {selectedMember.Profile.date_of_birth ? new Date(selectedMember.Profile.date_of_birth).toLocaleDateString() : 'N/A'}</p>
                <p><strong>Số điện thoại:</strong> {selectedMember.Profile.phone_number}</p>
                <h3>Lịch sử Gói tập</h3>
                <ul>
                    {selectedMember.MemberSubscriptions?.map(sub => (
                        <li key={sub.subscription_id}>
                            {sub.MembershipPackage.package_name} (Từ {new Date(sub.start_date).toLocaleDateString()} đến {new Date(sub.end_date).toLocaleDateString()}) - <strong>{sub.status}</strong>
                        </li>
                    ))}
                </ul>
            </div>
        );
    }

    // RENDER THE LIST VIEW (DEFAULT)
    return (
        <div className="management-page-container">
            <Link to="/dashboard" className="btn-back-dashboard">← Quay lại Dashboard</Link>
            <div className="page-header">
                <h2>Quản lý Danh sách Học viên</h2>
                <Link to="/staff/register-member"><button className="btn-add-new">Thêm Học viên mới</button></Link>
            </div>
            <div className="table-wrapper">
                <table className="data-table">
                    <thead>
                        <tr>
                            <th>Tên Học viên</th>
                            <th>Gói tập hiện tại</th>
                            <th>Ngày bắt đầu</th> 
                            <th>Trạng thái</th>   
                            <th>Hành động</th>
                        </tr>
                    </thead>
                    <tbody>
                        {members.length > 0 ? members.map(member => {
                            // Find the most recent subscription to display its info
                            const latestSubscription = member.MemberSubscriptions?.sort((a, b) => new Date(b.end_date) - new Date(a.end_date))[0];

                            return (
                                <tr key={member.user_id}>
                                    <td>{member.Profile?.full_name || 'N/A'}</td>
                                    <td>{latestSubscription?.MembershipPackage?.package_name || 'N/A'}</td>
                                    <td>
                                        {latestSubscription ? new Date(latestSubscription.start_date).toLocaleDateString() : 'N/A'}
                                    </td>
                                    <td>
                                        {/* --- 4. ADDED STATUS CELL (using the new activity_status field) --- */}
                                        <span className={`status-badge status-${(latestSubscription?.activityStatus || 'unknown').toLowerCase()}`}>
                                            {latestSubscription?.activityStatus || 'N/A'}
                                        </span>
                                    </td>
                                    <td className="action-buttons">
                                        <button className="btn-view" onClick={() => handleViewDetails(member.user_id)}>Xem</button>
                                        <button className="btn-edit" onClick={() => handleEditClick(member)}>Sửa</button>
                                        <button className="btn-delete" onClick={() => handleDelete(member.user_id)}>Xóa</button>
                                    </td>
                                </tr>
                            );
                        }) : (
                            <tr><td colSpan="5" className="no-data-cell">Chưa có học viên nào được phân công.</td></tr>
                        )}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default TrainerMemberManagementPage;
