import React, { useEffect, useState, useContext } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import AuthContext from '../contexts/AuthContext'; // Import AuthContext

const DashboardPage = () => {
    // const [user, setUser] = useState(null); // Replaced by context
    const { user, logout } = useContext(AuthContext); // Use user from context
    const navigate = useNavigate();

    useEffect(() => {
        // User is now managed by AuthContext, which handles localStorage
        if (!user) {
            // If AuthContext is still loading or user is not found, redirect
            // AuthContext's loading state could be used for a better loading experience
            const token = localStorage.getItem('token');
            if (!token) {
                 navigate('/login');
            }
            // If token exists but user is not yet in context, AuthContext might still be initializing
            // Or, if user is null after context loading, then redirect.
        }
    }, [user, navigate]);


    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    if (!user) {
        return <p>Loading dashboard or redirecting...</p>; // Or a loading spinner
    }

    // Render different content based on user role
    const renderDashboardContent = () => {
        const buttonStyle = {
            display: 'inline-block',
            padding: '10px 15px',
            margin: '5px',
            backgroundColor: '#007bff',
            color: 'white',
            textDecoration: 'none',
            borderRadius: '5px',
            border: 'none',
            cursor: 'pointer',
            textAlign: 'center',
            minWidth: '200px', // Ensure buttons have a decent width
        };

        switch (user.role) {
            case 'member':
                return (
                    <>
                        <h3>Welcome Member, {user.fullName}!</h3>
                        <h4>Your Functions:</h4>
                        <div>
                            <Link to="/member/payment" style={buttonStyle}>Thanh toán gói tập</Link>
                            <Link to="/member/profile" style={buttonStyle}>Xem và cập nhật thông tin cá nhân</Link>
                            <Link to="/member/history" style={buttonStyle}>Xem lịch sử tập luyện</Link>
                            <Link to="/member/feedback" style={buttonStyle}>Đánh giá chất lượng dịch vụ</Link>
                            <Link to="/packages/view" style={buttonStyle}>Xem thông tin các gói tập</Link>
                            <Link to="/member/booking" style={buttonStyle}>Đặt lịch tập với huấn luyện viên</Link>
                        </div>
                    </>
                );
            case 'staff':
                return (
                    <>
                        <h3>Welcome Staff, {user.fullName}!</h3>
                        <h4>Your Functions:</h4>
                        <div>
                            <Link to="/staff/equipment" style={buttonStyle}>Cập nhật tình trạng thiết bị</Link>
                            <Link to="/staff/register-member" style={buttonStyle}>Đăng ký hội viên mới</Link>
                            <Link to="/staff/renew-package" style={buttonStyle}>Gia hạn gói tập cho hội viên</Link>
                            <Link to="/staff/feedback-management" style={buttonStyle}>Xử lý phản hồi từ hội viên</Link>
                            <Link to="/staff/member-service-history" style={buttonStyle}>Ghi nhận lịch sử sử dụng dịch vụ</Link>
                        </div>
                    </>
                );
            case 'trainer':
                return (
                    <>
                        <h3>Welcome Trainer, {user.fullName}!</h3>
                        <h4>Your Functions:</h4>
                        <div>
                            <Link to="/trainer/my-members" style={buttonStyle}>Danh sách học viên</Link>
                            <Link to="/trainer/member-progress" style={buttonStyle}>Theo dõi và cập nhật tiến độ học viên</Link>
                            <Link to="/trainer/evaluate-member" style={buttonStyle}>Đánh giá kết quả luyện tập</Link>
                        </div>
                    </>
                );
            case 'owner':
                return (
                    <>
                        <h3>Welcome Owner, {user.fullName}! (Admin Dashboard)</h3>

                        <h4>Management Functions:</h4>
                        <h5>User Management</h5>
                        <div>
                            <Link to="/admin/manage-system" style={buttonStyle}>Quản lý tổng thể hệ thống</Link>
                            <Link to="/admin/manage-users" style={buttonStyle}>Quản trị người dùng</Link>
                        </div>

                        <h5>Gym Operations</h5>
                        <div>
                            <Link to="/admin/gym-info" style={buttonStyle}>Quản lý thông tin phòng tập (Under Development)</Link>
                            <Link to="/admin/manage-equipment" style={buttonStyle}>Quản lý thiết bị tập luyện</Link>
                            <Link to="/admin/manage-staff" style={buttonStyle}>Quản lý nhân sự (Under Development)</Link>
                            <Link to="/admin/manage-feedback" style={buttonStyle}>Quản lý phản hồi hội viên (Under Development)</Link>
                            <Link to="/admin/manage-packages" style={buttonStyle}>Quản lý gói tập (Under Development)</Link>
                        </div>

                        <h5>Reporting</h5>
                        <div>
                            <Link to="/admin/reports/revenue" style={buttonStyle}>Báo cáo doanh thu (Under Development)</Link>
                            <Link to="/admin/reports/registrations" style={buttonStyle}>Báo cáo đăng ký và gia hạn (Under Development)</Link>
                            <Link to="/admin/reports/staff-performance" style={buttonStyle}>Báo cáo hiệu suất nhân viên (Under Development)</Link>
                        </div>

                        <h5>Staff Functions (for Owner)</h5>
                        <div>
                            <Link to="/staff/equipment" style={buttonStyle}>Cập nhật tình trạng thiết bị</Link>
                            <Link to="/staff/register-member" style={buttonStyle}>Đăng ký hội viên mới</Link>
                            <Link to="/staff/renew-package" style={buttonStyle}>Gia hạn gói tập cho hội viên</Link>
                            <Link to="/staff/feedback-management" style={buttonStyle}>Xử lý phản hồi từ hội viên</Link>
                            <Link to="/staff/member-service-history" style={buttonStyle}>Ghi nhận lịch sử sử dụng dịch vụ</Link>
                        </div>
                    </>
                );
            default:
                return <h3>Welcome, {user.fullName}! Your role ({user.role}) is not fully configured for a dashboard view.</h3>;
        }
    };

    return (
        <div>
