import React from 'react';
import gymBackground from '../assets/images/gym-background.jpg';

const HomePage = () => {
  return (
    <>
      {/* Fullscreen background image */}
      <div
        style={{
          position: 'fixed',
          inset: 0,
          width: '100vw',
          height: '100vh',
          backgroundImage: `url(${gymBackground})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          zIndex: -2,
        }}
      />
      {/* Dark overlay for readability */}
      <div
        style={{
          position: 'fixed',
          inset: 0,
          width: '100vw',
          height: '100vh',
          background: 'rgba(20, 20, 30, 0.7)',
          zIndex: -1,
        }}
      />

      {/* Content overlay */}
      <div
        className="min-h-screen flex flex-col justify-center items-center text-gray-100 px-6 py-12"
        style={{ position: 'relative', zIndex: 1 }}
      >
        <div
          className="bg-gray-900 bg-opacity-80 p-8 rounded-lg max-w-xl text-center shadow-2xl
            transform transition-transform duration-700 ease-in-out
            hover:scale-105
            sm:p-12
            md:p-16"
        >
          <h1
            className="text-4xl sm:text-5xl md:text-6xl font-extrabold mb-6 animate-fadeInDown"
            style={{
              background: 'linear-gradient(90deg, #fffbe6 0%, #ffe066 40%, #ffd700 100%)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              textShadow: '0 4px 24px rgba(255, 255, 200, 0.7), 0 1px 2px #fff',
              letterSpacing: '2px',
              fontWeight: 900,
            }}
          >
            Welcome to Gym Management System
          </h1>
          <p className="mb-10 text-base sm:text-lg md:text-xl text-gray-300 animate-fadeInUp">
            Your one-stop solution for managing your gym operations efficiently.
          </p>
          <div className="flex flex-col sm:flex-row justify-center space-y-4 sm:space-y-0 sm:space-x-8 animate-fadeInUp delay-200">
            <a
              href="/"
              className="home-btn"
              style={{
                padding: '12px 32px',
                fontWeight: 700,
                fontSize: '1.1rem',
                borderRadius: '999px',
                background: 'linear-gradient(90deg, #43e97b 0%, #38f9d7 100%)',
                color: '#222',
                boxShadow: '0 4px 16px rgba(67,233,123,0.15)',
                textDecoration: 'none',
                transition: 'transform 0.2s, box-shadow 0.2s',
                marginRight: '8px'
              }}
            >
              Home
            </a>
            <a
              href="/register"
              className="register-btn"
              style={{
                padding: '12px 32px',
                fontWeight: 700,
                fontSize: '1.1rem',
                borderRadius: '999px',
                background: 'linear-gradient(90deg, #f7971e 0%, #ffd200 100%)',
                color: '#222',
                boxShadow: '0 4px 16px rgba(255,210,0,0.15)',
                textDecoration: 'none',
                transition: 'transform 0.2s, box-shadow 0.2s',
                marginRight: '8px'
              }}
            >
              Register
            </a>
            <a
              href="/login"
              className="login-btn"
              style={{
                padding: '12px 32px',
                fontWeight: 700,
                fontSize: '1.1rem',
                borderRadius: '999px',
                background: 'linear-gradient(90deg, #667eea 0%, #764ba2 100%)',
                color: '#fff',
                boxShadow: '0 4px 16px rgba(102,126,234,0.15)',
                textDecoration: 'none',
                transition: 'transform 0.2s, box-shadow 0.2s'
              }}
            >
              Login
            </a>
          </div>
        </div>
      </div>

      {/* Custom animation styles and button hover effects */}
      <style>{`
        @keyframes fadeInDown {
          0% {
            opacity: 0;
            transform: translateY(-20px);
          }
          100% {
            opacity: 1;
            transform: translateY(0);
          }
        }
        @keyframes fadeInUp {
          0% {
            opacity: 0;
            transform: translateY(20px);
          }
          100% {
            opacity: 1;
            transform: translateY(0);
          }
        }
        .animate-fadeInDown {
          animation: fadeInDown 0.8s ease forwards;
        }
        .animate-fadeInUp {
          animation: fadeInUp 0.8s ease forwards;
        }
        .delay-200 {
          animation-delay: 0.2s;
        }
        .home-btn:hover, .register-btn:hover, .login-btn:hover {
          transform: scale(1.07);
          box-shadow: 0 8px 32px rgba(0,0,0,0.18);
          filter: brightness(1.08);
        }
      `}</style>
    </>
  );
};

export default HomePage;
