import React from 'react';
import gymBackground from '../assets/images/gym-background.jpg';

const HomePage = () => {
  return (
    <div
      className="min-h-screen flex flex-col justify-center items-center text-gray-100 px-6 py-12
        bg-cover bg-center bg-no-repeat
        sm:px-12 sm:py-20
        md:px-24 md:py-32
        lg:px-40 lg:py-40
        "
      style={{
        backgroundImage: `url(${gymBackground})`,
      }}
    >
      <div className="bg-gray-900 bg-opacity-75 p-8 rounded-lg max-w-xl text-center shadow-2xl
        transform transition-transform duration-700 ease-in-out
        hover:scale-105
        sm:p-12
        md:p-16
        "
      >
        <h1 className="text-3xl sm:text-4xl md:text-5xl font-extrabold mb-6 text-yellow-300 animate-fadeInDown">
          Welcome to Gym Management System
        </h1>
        <p className="mb-10 text-base sm:text-lg md:text-xl text-gray-300 animate-fadeInUp">
          Your one-stop solution for managing your gym operations efficiently.
        </p>
        <div className="flex flex-col sm:flex-row justify-center space-y-4 sm:space-y-0 sm:space-x-8 animate-fadeInUp delay-200">
          <a
            href="/register"
            className="px-8 py-3 bg-yellow-500 hover:bg-yellow-600 rounded-md font-semibold transition-colors shadow-lg
              transform hover:-translate-y-1 hover:shadow-xl text-gray-900"
          >
            Register
          </a>
          <a
            href="/login"
            className="px-8 py-3 bg-indigo-600 hover:bg-indigo-700 rounded-md font-semibold transition-colors shadow-lg
              transform hover:-translate-y-1 hover:shadow-xl text-gray-100"
          >
            Login
          </a>
        </div>
      </div>

      {/* Custom animation styles */}
      <style>{`
        @keyframes fadeInDown {
          0% {
            opacity: 0;
            transform: translateY(-20px);
          }
          100% {
            opacity: 1;
            transform: translateY(0);
          }
        }
        @keyframes fadeInUp {
          0% {
            opacity: 0;
            transform: translateY(20px);
          }
          100% {
            opacity: 1;
            transform: translateY(0);
          }
        }
        .animate-fadeInDown {
          animation: fadeInDown 0.8s ease forwards;
        }
        .animate-fadeInUp {
          animation: fadeInUp 0.8s ease forwards;
        }
        .delay-200 {
          animation-delay: 0.2s;
        }
      `}</style>
    </div>
  );
};

export default HomePage;
